mapper.update:
update base_role
SET rolename = #{rolename,jdbcType=VARCHAR},
    rolecode = #{rolecode,jdbcType=INTEGER},
    clientcode = #{clientcode,jdbcType=VARCHAR},
    remark = #{remark,jdbcType=VARCHAR}
    WHERE guid = #{guid,jdbcType=VARCHAR}

mapper.select:
SELECT * FROM (SELECT

FROM base_role
where 1=1

    AND clientcode =#{entity.clientcode}


    AND rolename like CONCAT('%',#{entity.rolename},'%')

) t ORDER by rolecode
limit #{start},#{length}

SELECT count(*)
FROM base_role
WHERE rolecode = #{rolecode,jdbcType=INTEGER}
